import Moon from "../assets/icon-moon.svg";
import Sun from "../assets/icon-sun.svg";
import SearchIcon from "../assets/icon-search.svg";
import Location from "../assets/icon-location.svg";
import Website from "../assets/icon-website.svg";
import Twitter from "../assets/icon-twitter.svg";
import Company from "../assets/icon-company.svg";
import { useState } from "react";


const Searcher = () => {
    const [darkMode, setDarkMode] = useState(false);
    const [username, setUsername] = useState("");
    const [showError, setShowError] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try{
            const response = await fetch(`https://api.github.com/users/${username}`)
            const data = await response.json();
            console.log(data);
        } catch (error) {
            setShowError(true);
        }
    };

  return (
    <div id="container" className={darkMode ? 'dark-mode' : ''}>
      <header id="header">
        <h1 id="title">devfinder</h1>
        <button typ="button" id="btn-mode" onClick={() => setDarkMode(!darkMode)}>
  <p id="mode-text">{darkMode ? 'Light' : 'Dark'}</p>
          <img id="moonIcon" src={darkMode ? Sun : Moon} alt="Icon Sun/Moon" />
        </button>
      </header>
      <form>
        <input
          type="text"
          placeholder="Search GitHub username..."
          id="search-input"
          style={{
            backgroundImage: `url(${SearchIcon})`,
            backgroundRepeat: "no-repeat",
            backgroundPosition: "5% center",
            paddingLeft: "14%",
          }}
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        {showError && <div id="error-message" style={{ color: "red", marginLeft: "5px" }}>No results</div>}
        <button type="submit" id="search-button" onClick={handleSubmit}>
          Search
        </button>
      </form>
      <div id="main">
        <div id="profile-section">
          <div id="profile-container"></div>
          <div id="username-section">
            <h3 id="user-name">The Octocat</h3>
            <a href="/" id="user-name-link">
              @octocat
            </a>
        <p id="join-date">Joined 25 Jan 2011</p>
          </div>
        </div>
        <p id="user-description">Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Donec odio. Quisque volutpat mattis eros.</p>
      <div id="grid-div">
        <div id="repos">Repos</div>
        <div id="followers">Followers</div>
        <div id="following">Following</div>
        <div id="repos-number">8</div>
        <div id="followers-number">900909</div>
        <div id="following-number">78789</div>
      </div>
      <div id="links-grid">
        <img id="location" src={Location} alt="Icon Location" />
        <a  href="/" id="location-link">
          San Francisco
        </a>
        <img id="website" src={Website} alt="Icon Website" />
        <a  href="/" id="website-link">
        https://github.blog
        </a>
        <img id="twitter" src={Twitter} alt="Icon Twitter" />
        <a  href="/" id="twitter-link">
          Not Available
        </a>
        <img id="company" src={Company} alt="Icon company" />
        <a  href="/" id="company-link">
          @github
        </a>
      </div>
      </div>
    </div>
  );
};

export default Searcher;
